commits.content:
.header {
    height: 55px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;

    position: fixed;
    top: 0;
    left: 0;
    left: 0;
    right: 0;

    background-color: white;

}


.left-section {

    display: flex;
    align-items: center;
}

.hamburger-menu {

    height: 24px;
    margin-left: 24px;
    margin-right: 24px;
}


.youtube-logo {
    height: 20px ;
}

.middle-section {
    flex: 1;
    margin-left: 70px;
    margin-right:  35px;
    max-width: 500px;
    display: flex;
    align-items: center;
}

.search-bar{
    flex: 1;
    height: 36px;
    padding-left: 10px;
    font-size: 16px;
    border-width: 1px;
    border-style: solid;
    border-color: rgb(192, 192, 192);
    border-radius: 2px;
    box-shadow: inset 1px 2px 3px rgba(0, 0, 0, 0.05);
    width: 0;

}   



.search-bar::placeholder {
    font-family: Roboto,Arial;
    font-size: 16px;
}


.search-button{
    height: 40px;
    width: 66px;
    background-color: rgb(240, 240, 240);
    border-width: 1px;
    border-style: solid;
    border-color: rgb(192, 192, 192);
    margin-left: -1px;
    margin-right: 10px;
}


.search-icon {
    height: 25px;
    margin-top: 4px;
}


.voice-search-button{
    height: 40px;
    width: 40px;
    border-radius: 20px;
    border: none;
    background-color: rgb(245, 245, 245);
}


.video-search-icon {
    height: 24px;
    width: 40px;
    border-radius: 20px;
    border: none;
    background-color: rgb(245, 245, 245);
}

.right-section {
    width: 180px;
    margin-right: 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-shrink: 0;

}

.upload-icon {
    height: 24px;
}

.youtube-apps-icon{
    height: 24px;
}


.notifications-icon {
    height: 24px;
}

.current-user-picture {
    height: 32px;
    border-radius: 16px ;
}
